import React from "react";

// reactstrap components
import {Container, Row, Col, Button} from "reactstrap";

// core components

class BtnCategory extends React.Component {


    constructor(props) {
        super(props);
        this.state = {
            name: props.name,
            btnCategory: <></>
        }
        this.handleClick = this.handleClick.bind(this)

    }

    handleClick() {
        this.setState((prevState) => {
            return {
                btnCategory: <BtnCategory name={this.state.name} />,
                name: "test"
            }
        })
    }

    render() {


        return (

            <>
                <Button className="bg-category-btn" size="lg" onClick={this.handleClick}>
                    {this.state.name}
                </Button>
                <div>
                    {this.state.btnCategory}
                </div>

            </>
        );


    }
}

export default BtnCategory;
