import React, { Component }  from "react";

import Book from "./Book";
import ReactCardCarousel from 'react-card-carousel';
import booksTestData from "views/examples/booksTestData.js";

// core components

class BookGalery extends Component {

    static get CARD_STYLE() {
      return {
        height: '200px',
        width: '200px',
        paddingTop: '80px',
        textAlign: 'center',
        background: '#52C0F5',
        color: '#FFF',
        fontSize: '12px',
        textTransform: 'uppercase',
        borderRadius: '10px',
      };
    }


  render(props) {
    const booksData = booksTestData.map(book => <Book src={book.src} key = {book.id}  author={book.author} />)
    console.log(props.cat)
    return (
        <>
        <ReactCardCarousel autoplay={ true } autoplay_speed={ 2500 }>
            <div className="custom-control-inline" style={ BookGalery.CARD_STYLE } >
                {booksData}
            </div>
        </ReactCardCarousel>
        </>
    );
}
}

export default BookGalery;
