import React from "react";

// reactstrap components
import {Container, Row, Col, Button} from "reactstrap";
import CategoryBtns from "./CategoryBtns";

// core components

class BtnCategory extends React.Component {


    constructor(props) {
        super(props);
        this.state = {
            name: props.name,
            btnCategory: <></>,
            isHidden: false
        }
        this.handleClick = this.handleClick.bind(this)
        this.handleClick1 = this.handleClick1.bind(this)
        this.handleClick2 = this.handleClick2.bind(this)
        this.test = this.test.bind(this)

    }

    handleClick() {

        this.setState((prevState) => {
            return {
                name: this.state.name.substr(0,this.state.name.length-1 ),
                btnCategory:
                    <div >
                        <Button size="lg" onClick={this.handleClick}>
                            {this.state.name.substr(0,this.state.name.length-1 )}
                        </Button>
                        <p/>
                        <div className="custom-control-inline">
                        <Button size="lg" onClick={this.handleClick1}>
                            {this.state.name+1}
                        </Button>
                        <Button size="lg" onClick={this.handleClick2}>
                            {this.state.name+2}
                        </Button>
                        </div>

                    </div>,
                isHidden: true
            }
        })
        this.test()
    }
    handleClick1() {

        this.test(1)
    }
    handleClick2() {

        this.test(2)
    }
test(count){
    this.setState((prevState) => {
        return {
            name: this.state.name + count,
            btnCategory:
                <div >
                    <Button size="lg" onClick={this.handleClick}>
                        {this.state.name }
                    </Button>
                    <p/>
                    <div className="custom-control-inline">
                        <Button size="lg" onClick={this.handleClick1}>
                            {this.state.name + "1"}
                        </Button>
                        <Button size="lg" onClick={this.handleClick2}>
                            {this.state.name + "2"}
                        </Button>
                    </div>

                </div>,
            isHidden: true
        }
    })
}


    render() {


        return (

            <Container>
                {
                    !this.state.isHidden &&
                    <Button size="lg" onClick={this.handleClick}>
                        {this.props.name}
                    </Button>
                }
                { console.log(this.state)}
                {this.state.btnCategory}


            </Container>
        );


    }
}

export default BtnCategory;
