import React from "react";

// reactstrap components
import {Container, Row, Col, Button} from "reactstrap";
import categoryData from "../examples/categoryData";
import BtnCategory from "./BtnCategory";

// core components

class CategoryBtns extends React.Component {

    constructor(props) {
        super(props)
        this.state = {
            btnCategory: "init"
            //btnCategory: categoryData.map(category => <BtnCategory name = {category.name} onClick="handelClick" />)
        }
        this.handelClick = this.handelClick.bind(this)
        this.changeName = this.changeName.bind(this)
    }
    changeName(name){
        this.setState(() => {
            return {
                btnCategory: name
            }
        })
    }
    handelClick(){
        this.changeName("test")
    }

    render() {
        return (

            <>
                <div className="bg-white" size="lg">
                    <BtnCategory name = {this.state.btnCategory} onClick={this.handelClick()} />
                </div>
            </>
        );
    }
}

export default CategoryBtns;
