import React from "react";

// reactstrap components
import {Container, Row, Col, Button} from "reactstrap";
import CategoryBtns from "./CategoryBtns";

// core components

class BtnCategory extends React.Component {


    constructor(props) {
        super(props);
        this.state = {
            name: props.name,
            btnCategory: <></>,
            isHidden: false
        }
        this.handleClick = this.handleClick.bind(this)

    }

    handleClick() {
        this.setState((prevState) => {
            return {
                btnCategory: <div> <BtnCategory name={prevState.name + "1"}  /> <BtnCategory name={prevState.name + "2"}  /> </div>,
                name: prevState.name + "a",
                isHidden: !this.state.isHidden
            }
        })
    }

    render() {


        return (

            <Container>
                {
                    !this.state.isHidden &&
                    <Button  size="lg" onClick={this.handleClick}>
                        {this.props.name}
                    </Button>
                }
                {this.state.btnCategory}

            </Container>
        );


    }
}

export default BtnCategory;
