import React from "react";

// reactstrap components
import {Container, Row, Col, Button} from "reactstrap";
import CategoryBtns from "./CategoryBtns";

// core components

class BtnCategory extends React.Component {


    constructor(props) {
        super(props);
        this.state = {
            name: props.name,
            btnCategory: <><Button style= {{ backgroundColor: this.color }}   size="lg" onClick={this.handleClick}>
                {this.props.name}
            </Button></>,
            color: (191,111,87)
        }
        this.handleClick = this.handleClick.bind(this)

    }

    handleClick() {
        this.setState((prevState) => {
            return {
                btnCategory:  <BtnCategory name={"test"}  />,
                name: prevState.name + "a"
            }
        })
    }

    render() {


        return (

            <Container>
                <Button style= {{ backgroundColor: this.color }}   size="lg" onClick={this.handleClick}>
                    {this.props.name}
                </Button>
                {this.state.btnCategory}

            </Container>
        );


    }
}

export default BtnCategory;
