import React from "react";

// reactstrap components
import {Container, Row, Col, Button} from "reactstrap";
import categoryData from "../examples/categoryData";
import BtnCategory from "./BtnCategory";

// core components

class CategoryBtns extends React.Component {

    constructor(props) {
        super(props)
        this.state = {
            //btnCategory: categoryData.map(category => <BtnCategory name = {category.name} onClick="handelClick" />)
            questions: [
                { id: 'fdsd', title: 'Why is the sky blue?' },
                { id: 'adsf', title: 'Who invented pizza?' },
                { id: 'afdsf', title: 'Is green tea overrated?' },
            ],
            displayQuestions: false
        }
        this.handleClick = this.handleClick.bind(this)
    }
    handleClick() {
        this.setState(prevState => {
            return {
                btnCategory: prevState.btnCategory + 1
            }
        })
    }


    displayQuestion = () => {
        this.setState({
            displayQuestions: !this.state.displayQuestions
        })
    }

    render() {
        if ( this.state.displayQuestions ) {
            let questions = (
                <div>
                    { this.state.questions.map((question, index) => {
                        return <BtnCategory key={question.id}
                                         title={question.title} />
                    })}
                </div>
            )
        }
        return (

            <>
                <button className="bg-white" size="lg" onClick={this.handleClick}>
                    <p> {this.state.btnCategory}  </p>
                </button>



        <div className="App">
            <h1>Question Genie</h1>
            <button className="btn" onClick={this.displayQuestion}>View     Unanswered Questions</button>
            {questions}
        </div>
            </>
        );
    }
}

export default CategoryBtns;
